INSERT INTO OPR_COURSECOSTDIV ( ORGANCODE , COSTYYMM , SEQ , COURSECODE, COUSRENAME, EDUCOUNT , EDUCOST , INSUSER , INSDATE )SELECT B.CHIEFORGAN, REPLACE(#searchdate#,'-',''), #seq#, #coursecode#, DECODE(#coursecode#,'C','민간컨텐츠과정('||TO_NUMBER(SUBSTR(REPLACE(#searchdate#,'-',''),5,2))||'월)',(SELECT COURSENAME||'('||TO_NUMBER(SUBSTR(COURSECODE,11,2))||'기)' FROM OPR_LECTURE WHERE COURSECODE= #coursecode#)) COURSENAME, DECODE(A.CHIEFORGAN, B.CHIEFORGAN, A.ORGCNT, 0) ORGCNT, DECODE(A.CHIEFORGAN, B.CHIEFORGAN, DECODE(B.CHIEFORGAN, '6430000', NVL(A.EDUCOST,0)+NVL(C.REMAIN,0), NVL(A.EDUCOST,0)), 0) EDUCOST, #userId#, TO_CHAR(SYSDATE,'YYYYMMDD') FROM (SELECT A.COURSECODE, A.CHIEFORGAN, COUNT(A.JUMINNO) ORGCNT, TRUNC(TRUNC(MAX(C.TOTALCOST)/MAX(C.TOTALINWON))*COUNT(A.JUMINNO),-1) AS EDUCOST FROM OPR_CRSSTUDENT A, (SELECT COURSECODE, SUM(NVL(TXTBOOKCOST_INTRO,0)) + SUM(NVL(TXTBOOKCOST_MSCT,0)) + SUM(NVL(TXTBOOKCOST_COMMON,0)) + SUM(NVL(TXTBOOKCOST_AXBOOK,0)) + SUM(NVL(PROFCOST_PAY,0)) + SUM(NVL(PROFCOST_TRAVEL,0)) + SUM(NVL(PROFCOST_QUESTION,0)) + SUM(NVL(IMCHACOST,0)) + SUM(NVL(ETCCOST,0)) TOTALCOST, (SELECT COUNT(JUMINNO) FROM OPR_CRSSTUDENT B WHERE A.COURSECODE = B.COURSECODE AND (ISCOST = 'N' OR ISCOST IS NULL)) TOTALINWON FROM OPR_COURSECOST A WHERE A.COSTYYMM = REPLACE(#searchdate#,'-','') AND A.COURSECODE = #coursecode# GROUP BY COURSECODE ) C WHERE A.COURSECODE = C.COURSECODE AND (A.ISCOST = 'N' OR A.ISCOST IS NULL) AND A.COURSECODE = #coursecode# GROUP BY A.COURSECODE, A.CHIEFORGAN
UNION ALL SELECT 'C', CHIEFORGAN, COUNT(B.JUMINNO), TRUNC(TRUNC(MAX(A.TOTALCOST)/MAX(A.TOTALINWON)) * COUNT(B.JUMINNO),-1) FROM (SELECT COURSECODE, (SELECT MAX(NVL(EDUCOST,0)) FROM OPR_CYBERCOSTMST A, OPR_CYBERCOST B WHERE A.CYBERYYMM = B.CYBERYYMM AND A.CYBERTIMES = B.CYBERTIMES AND A.COSTYYMM = REPLACE(#searchdate#,'-','') )TOTALCOST, (SELECT COUNT(JUMINNO) FROM OPR_CYBERCOSTMST A, OPR_CYBERCOST B, OPR_CRSSTUDENT C WHERE A.CYBERYYMM = B.CYBERYYMM AND A.CYBERTIMES = B.CYBERTIMES AND A.COSTYYMM = REPLACE(#searchdate#,'-','') AND B.COURSECODE = C.COURSECODE AND C.ISFINISH LIKE 'Y' AND (ISCOST = 'N' OR ISCOST IS NULL) ) TOTALINWON FROM OPR_CYBERCOSTMST A, OPR_CYBERCOST B WHERE A.CYBERYYMM = B.CYBERYYMM AND A.CYBERTIMES = B.CYBERTIMES AND A.COSTYYMM = REPLACE(#searchdate#,'-','') GROUP BY COURSECODE ) A, OPR_CRSSTUDENT B WHERE A.COURSECODE = B.COURSECODE AND B.ISFINISH LIKE 'Y' AND (B.ISCOST = 'N' OR B.ISCOST IS NULL) GROUP BY CHIEFORGAN
) A, (SELECT COURSECODE, MAX(TOTALCOST)-SUM(EDUCOST) REMAIN FROM (SELECT A.COURSECODE, A.CHIEFORGAN, COUNT (A.JUMINNO) ORGCNT, MAX(TOTALCOST) TOTALCOST, TRUNC(TRUNC(MAX(B.TOTALCOST)/MAX(B.TOTALINWON)) * COUNT(A.JUMINNO),-1) EDUCOST FROM OPR_CRSSTUDENT A, (SELECT COURSECODE, SUM(NVL(TXTBOOKCOST_INTRO,0)) + SUM(NVL(TXTBOOKCOST_MSCT,0)) + SUM(NVL(TXTBOOKCOST_COMMON,0)) + SUM(NVL(TXTBOOKCOST_AXBOOK,0)) + SUM(NVL(PROFCOST_PAY,0)) + SUM(NVL(PROFCOST_TRAVEL,0)) + SUM(NVL(PROFCOST_QUESTION,0)) + SUM(NVL(IMCHACOST,0)) + SUM(NVL(ETCCOST,0)) TOTALCOST, (SELECT COUNT(JUMINNO) FROM OPR_CRSSTUDENT B WHERE A.COURSECODE = B.COURSECODE AND (ISCOST = 'N' OR ISCOST IS NULL)) TOTALINWON FROM OPR_COURSECOST A WHERE COSTYYMM = REPLACE(#searchdate#,'-','') AND COURSECODE = #coursecode# GROUP BY COURSECODE ) B WHERE A.COURSECODE = B.COURSECODE AND A.COURSECODE = #coursecode# AND (A.ISCOST = 'N' OR A.ISCOST IS NULL) GROUP BY A.COURSECODE, A.CHIEFORGAN
UNION ALL SELECT 'C', CHIEFORGAN, COUNT(B.JUMINNO), MAX(A.TOTALCOST), TRUNC(TRUNC(MAX(A.TOTALCOST)/MAX(A.TOTALINWON)) * COUNT(B.JUMINNO),-1) FROM (SELECT COURSECODE, (SELECT MAX(NVL(EDUCOST,0)) FROM OPR_CYBERCOSTMST A, OPR_CYBERCOST B WHERE A.CYBERYYMM = B.CYBERYYMM AND A.CYBERTIMES = B.CYBERTIMES AND A.COSTYYMM = REPLACE(#searchdate#,'-','') )TOTALCOST, (SELECT COUNT(JUMINNO) FROM OPR_CYBERCOSTMST A, OPR_CYBERCOST B, OPR_CRSSTUDENT C WHERE A.CYBERYYMM = B.CYBERYYMM AND A.CYBERTIMES = B.CYBERTIMES AND A.COSTYYMM = REPLACE(#searchdate#,'-','') AND B.COURSECODE = C.COURSECODE AND C.ISFINISH LIKE 'Y' AND (ISCOST = 'N' OR ISCOST IS NULL) ) TOTALINWON FROM OPR_CYBERCOSTMST A, OPR_CYBERCOST B WHERE A.CYBERYYMM = B.CYBERYYMM AND A.CYBERTIMES = B.CYBERTIMES AND A.COSTYYMM = REPLACE(#searchdate#,'-','') GROUP BY COURSECODE ) A, OPR_CRSSTUDENT B WHERE A.COURSECODE = B.COURSECODE AND B.ISFINISH LIKE 'Y' AND (B.ISCOST = 'N' OR B.ISCOST IS NULL) GROUP BY CHIEFORGAN
) GROUP BY COURSECODE ) C, CMN_MSTORGAN B WHERE B.CHIEFORGAN = A.CHIEFORGAN(+) AND A.COURSECODE = C.COURSECODE(+) ORDER BY A.COURSECODE, B.ORGANGU, B.CHIEFORGAN
