sqlMap.select:
SELECT ROWNUM NUM, A.SURVEYNO , A.REQSEQ , DECODE(A.REPLYTYPE,6,(SELECT MIN( REQSEQ ) REQ1 FROM TST_SURVITEM WHERE SURVEYNO = #surveyno# AND REPLYTYPE = 6 ), A.REQSEQ) DISREQSEQ, A.ITEMGROUP , A.ITEMCONTENTS , A.REPLYTYPE, A.ETCFLAG, NVL(B.PROFNO,'') PROFNO, NVL(B.ITEMCONTENTS,'') PROFNAME, NVL(B.SUBJNAME,'') SUBJNAME, A.SCOREFLAG, DECODE(A.REPLYTYPE, '5', (SELECT COUNT(B.REPLYSEQ) FROM TST_LECTRSPT B, (SELECT SURVEYNO, REPLYSEQ FROM TST_RSPTINFO
WHERE COURSECODE LIKE #coursecode#||'%'
) C WHERE A.SURVEYNO=B.SURVEYNO AND A.REQSEQ = B.REQSEQ AND B.SURVEYNO = C.SURVEYNO AND B.REPLYSEQ = C.REPLYSEQ AND B.COURSECODE=#coursecode# ), '6',(SELECT COUNT(D.REPLYSEQ) FROM TST_TEACHERRSPT D, (SELECT SURVEYNO, REPLYSEQ FROM TST_RSPTINFO
WHERE COURSECODE LIKE #coursecode#||'%'
) C WHERE B.SURVEYNO=D.SURVEYNO AND B.REQSEQ = D.REQSEQ AND B.COURSECODE = D.COURSECODE AND B.PROFNO = D.PROFNO AND D.SURVEYNO = C.SURVEYNO AND D.REPLYSEQ = C.REPLYSEQ AND D.COURSECODE=#coursecode# ), (SELECT COUNT(B.REPLYSEQ) FROM TST_COMMRSPT B, (SELECT SURVEYNO, REPLYSEQ FROM TST_RSPTINFO
WHERE COURSECODE LIKE #coursecode#||'%'
) C WHERE A.SURVEYNO = B.SURVEYNO AND A.REQSEQ = B.REQSEQ AND B.SURVEYNO = C.SURVEYNO AND B.REPLYSEQ= C.REPLYSEQ ) ) ITEMCNT, DECODE(A.SCOREFLAG,'Y',(SELECT ROUND(SUM((SELECT MAX(EXAMSEQ)+1 FROM TST_SURVITEMEXAM X WHERE X.SURVEYNO = D.SURVEYNO AND X.REQSEQ = D.REQSEQ )-D.REPLY)/COUNT(D.REPLYSEQ),1) FROM TST_COMMRSPT D, (SELECT SURVEYNO, REPLYSEQ FROM TST_RSPTINFO
WHERE COURSECODE LIKE #coursecode#||'%'
) C WHERE A.SURVEYNO=D.SURVEYNO AND A.REQSEQ = D.REQSEQ AND D.SURVEYNO = C.SURVEYNO AND D.REPLYSEQ= C.REPLYSEQ ), DECODE(REPLYTYPE, '5',(SELECT SUM(DECODE(REPLY,'1', 5, '2', 4, '3', 3, '4', 2,1))/COUNT(B.REPLYSEQ) FROM TST_LECTRSPT B, (SELECT SURVEYNO, REPLYSEQ FROM TST_RSPTINFO
WHERE COURSECODE LIKE #coursecode#||'%'
) C WHERE A.SURVEYNO=B.SURVEYNO AND A.REQSEQ = B.REQSEQ AND B.SURVEYNO = C.SURVEYNO AND B.REPLYSEQ= C.REPLYSEQ AND B.COURSECODE=#coursecode# ), '6',(SELECT SUM(DECODE(REPLY,'1', 5, '2', 4, '3', 3, '4', 2,1))/COUNT(D.REPLYSEQ) FROM TST_TEACHERRSPT D, (SELECT SURVEYNO, REPLYSEQ FROM TST_RSPTINFO
WHERE COURSECODE LIKE #coursecode#||'%'
) C WHERE B.SURVEYNO=D.SURVEYNO AND B.REQSEQ = D.REQSEQ AND B.COURSECODE = D.COURSECODE AND B.PROFNO = D.PROFNO AND D.SURVEYNO = C.SURVEYNO AND D.REPLYSEQ= C.REPLYSEQ AND D.COURSECODE=#coursecode# ), 0)) ITEMSCORE, (SELECT MIN(LECTDATE)||MIN(TIMESEQ) FROM OPR_LECTPROF WHERE COURSECODE=#coursecode# AND PROFNO = B.PROFNO GROUP BY COURSECODE, PROFNO ) OTHER FROM TST_SURVITEM A, TST_TEACHERITEM B WHERE A.SURVEYNO = B.SURVEYNO(+) AND A.REQSEQ = B.REQSEQ(+) AND A.SURVEYNO = #surveyno# AND B.COURSECODE(+) = #coursecode# ORDER BY 4, OTHER, PROFNO, REQSEQ

SELECT ROWNUM NUM, SURVEYNO , REQSEQ , ITEMGROUP , ITEMCONTENTS , REPLYTYPE, ETCFLAG, SCOREFLAG, DECODE(REPLYTYPE, '5', (SELECT COUNT(B.REPLYSEQ) FROM TST_LECTRSPT B, (SELECT SURVEYNO, REPLYSEQ FROM TST_RSPTINFO
WHERE COURSECODE LIKE #coursecode#||'%'
) C WHERE A.SURVEYNO=B.SURVEYNO AND A.REQSEQ = B.REQSEQ AND B.SURVEYNO = C.SURVEYNO AND B.REPLYSEQ = C.REPLYSEQ AND B.COURSECODE=#coursecode# ), (SELECT COUNT(B.REPLYSEQ) FROM TST_COMMRSPT B, (SELECT SURVEYNO, REPLYSEQ FROM TST_RSPTINFO
WHERE COURSECODE LIKE #coursecode#||'%'
) C WHERE A.SURVEYNO = B.SURVEYNO AND A.REQSEQ = B.REQSEQ AND B.SURVEYNO = C.SURVEYNO AND B.REPLYSEQ= C.REPLYSEQ ) ) ITEMCNT, DECODE(A.SCOREFLAG,'Y',(SELECT ROUND(SUM((SELECT MAX(EXAMSEQ)+1 FROM TST_SURVITEMEXAM X WHERE X.SURVEYNO = D.SURVEYNO AND X.REQSEQ = D.REQSEQ )-D.REPLY)/COUNT(D.REPLYSEQ),1) FROM TST_COMMRSPT D, (SELECT SURVEYNO, REPLYSEQ FROM TST_RSPTINFO
WHERE COURSECODE LIKE #coursecode#||'%'
) C WHERE A.SURVEYNO=D.SURVEYNO AND A.REQSEQ = D.REQSEQ AND D.SURVEYNO = C.SURVEYNO AND D.REPLYSEQ= C.REPLYSEQ ), DECODE(REPLYTYPE, '5',(SELECT SUM(DECODE(REPLY,'1', 5, '2', 4, '3', 3, '4', 2,1))/COUNT(B.REPLYSEQ) FROM TST_LECTRSPT B, (SELECT SURVEYNO, REPLYSEQ FROM TST_RSPTINFO
WHERE COURSECODE LIKE #coursecode#||'%'
) C WHERE A.SURVEYNO=B.SURVEYNO AND A.REQSEQ = B.REQSEQ AND B.SURVEYNO = C.SURVEYNO AND B.REPLYSEQ= C.REPLYSEQ AND B.COURSECODE=#coursecode# ),0)) ITEMSCORE FROM TST_SURVITEM A WHERE SURVEYNO = #surveyno# ORDER BY REQSEQ

SELECT SURVEYNO , REQSEQ , EXAMSEQ , EXAMCONTENTS, (SELECT COUNT(B.REPLYSEQ) FROM TST_COMMRSPT B, (SELECT SURVEYNO, REPLYSEQ FROM TST_RSPTINFO
WHERE COURSECODE LIKE #coursecode#||'%'
) C WHERE A.SURVEYNO = B.SURVEYNO AND A.REQSEQ = B.REQSEQ AND INSTR(','||REPLACE(B.REPLY,',', ',,')||',', ','||A.EXAMSEQ||',') >0 AND B.SURVEYNO = C.SURVEYNO AND B.REPLYSEQ= C.REPLYSEQ ) EXAMCNT FROM TST_SURVITEMEXAM A WHERE SURVEYNO = #surveyno# AND REQSEQ = #reqseq# ORDER BY REQSEQ, EXAMSEQ

SELECT SURVEYNO, REQSEQ, EXAMSEQ, EXAMCONTENTS, (SELECT COUNT(B.REPLYSEQ) FROM TST_LECTRSPT B, (SELECT SURVEYNO, REPLYSEQ FROM TST_RSPTINFO
WHERE COURSECODE LIKE #coursecode#||'%'
) C WHERE A.SURVEYNO = B.SURVEYNO AND A.REQSEQ = B.REQSEQ AND A.EXAMSEQ = B.REPLY AND B.SURVEYNO = C.SURVEYNO AND B.REPLYSEQ= C.REPLYSEQ AND B.COURSECODE=#coursecode# ) EXAMCNT FROM (SELECT #surveyno# SURVEYNO, #reqseq# REQSEQ, 1 EXAMSEQ, '매우만족' EXAMCONTENTS FROM DUAL UNION ALL SELECT #surveyno#, #reqseq#, 2 EXAMSEQ, '만족' EXAMCONTENTS FROM DUAL UNION ALL SELECT #surveyno#, #reqseq#, 3 EXAMSEQ, '보통' EXAMCONTENTS FROM DUAL UNION ALL SELECT #surveyno#, #reqseq#, 4 EXAMSEQ, '불만족' EXAMCONTENTS FROM DUAL UNION ALL SELECT #surveyno#, #reqseq#, 5 EXAMSEQ, '매우불만족' EXAMCONTENTS FROM DUAL ) A

SELECT A.SURVEYNO , REQSEQ , OTHER FROM TST_COMMRSPT A, (SELECT SURVEYNO, REPLYSEQ FROM TST_RSPTINFO WHERE SURVEYNO = #surveyno#
AND COURSECODE LIKE #coursecode#||'%'
) C WHERE A.SURVEYNO = C.SURVEYNO AND A.REPLYSEQ = C.REPLYSEQ AND A.SURVEYNO = #surveyno# AND REQSEQ = #reqseq#

SELECT A.SURVEYNO , REQSEQ , REPLY FROM TST_COMMRSPT A, (SELECT SURVEYNO, REPLYSEQ FROM TST_RSPTINFO WHERE SURVEYNO = #surveyno#
AND COURSECODE LIKE #coursecode#||'%'
) C WHERE A.SURVEYNO = C.SURVEYNO AND A.REPLYSEQ = C.REPLYSEQ AND A.SURVEYNO = #surveyno# AND REQSEQ = #reqseq#

SELECT ROWNUM NUM, SURVEYNO , REQSEQ , COURSECODE, PROFNO, ITEMCONTENTS PROFNAME, SUBJNAME, DISPLAYNO, (SELECT COUNT(B.REPLYSEQ) FROM TST_TEACHERRSPT B, (SELECT SURVEYNO, REPLYSEQ FROM TST_RSPTINFO
WHERE COURSECODE LIKE #coursecode#||'%'
) C WHERE A.SURVEYNO=B.SURVEYNO AND A.REQSEQ = B.REQSEQ AND B.COURSECODE=A.COURSECODE AND B.PROFNO = A.PROFNO AND B.SURVEYNO = C.SURVEYNO AND B.REPLYSEQ= C.REPLYSEQ ) TEACHERCNT, (SELECT SUM(DECODE(REPLY,'1', 5, '2', 4, '3', 3, '4', 2,1))/COUNT(B.REPLYSEQ) FROM TST_TEACHERRSPT B, (SELECT SURVEYNO, REPLYSEQ FROM TST_RSPTINFO
WHERE COURSECODE LIKE #coursecode#||'%'
) C WHERE A.SURVEYNO=B.SURVEYNO AND A.REQSEQ = B.REQSEQ AND B.COURSECODE=A.COURSECODE AND B.PROFNO = A.PROFNO AND B.SURVEYNO = C.SURVEYNO AND B.REPLYSEQ= C.REPLYSEQ ) TEACHERSCORE FROM TST_TEACHERITEM A WHERE SURVEYNO = #surveyno# AND REQSEQ = #reqseq# AND COURSECODE = #coursecode# ORDER BY REQSEQ

SELECT SURVEYNO, REQSEQ, COURSECODE, PROFNO, EXAMSEQ, EXAMCONTENTS, (SELECT COUNT(B.REPLYSEQ) FROM TST_TEACHERRSPT B, (SELECT SURVEYNO, REPLYSEQ FROM TST_RSPTINFO
WHERE COURSECODE LIKE #coursecode#||'%'
) C WHERE A.SURVEYNO = B.SURVEYNO AND A.REQSEQ = B.REQSEQ AND B.COURSECODE=A.COURSECODE AND B.PROFNO = A.PROFNO AND A.EXAMSEQ = B.REPLY AND B.SURVEYNO = C.SURVEYNO AND B.REPLYSEQ= C.REPLYSEQ ) EXAMCNT FROM (SELECT #surveyno# SURVEYNO, #reqseq# REQSEQ, #coursecode# COURSECODE, #profno# PROFNO, 1 EXAMSEQ, '매우만족' EXAMCONTENTS FROM DUAL UNION ALL SELECT #surveyno#, #reqseq#, #coursecode#,#profno#, 2 EXAMSEQ, '만족' EXAMCONTENTS FROM DUAL UNION ALL SELECT #surveyno#, #reqseq#, #coursecode#, #profno#, 3 EXAMSEQ, '보통' EXAMCONTENTS FROM DUAL UNION ALL SELECT #surveyno#, #reqseq#, #coursecode#, #profno#, 4 EXAMSEQ, '불만족' EXAMCONTENTS FROM DUAL UNION ALL SELECT #surveyno#, #reqseq#, #coursecode#, #profno#, 5 EXAMSEQ, '매우불만족' EXAMCONTENTS FROM DUAL ) A
